mapper.select:
SELECT TB.BNO , TB.TITLE , TB.CONTENT , CASE WHEN TM.LEFT = '1' THEN RPAD(SUBSTR(TB.USER_ID,1,1),LENGTH(TB.USER_ID),'*') ELSE USER_ID END AS USER_ID , TO_CHAR(TB.REGDATE,'YYYY-MM-DD HH24:MI') AS REGDATE , TO_CHAR(TB.UPDATEDATE,'YYYY-MM-DD HH24:MI') AS UPDATEDATE , TB.SECRET FROM ( SELECT /*+ INDEX_DESC(TBL_BOARD) */ ROWNUM RN, BNO, TITLE, CONTENT, USER_ID, REGDATE, UPDATEDATE, SECRET FROM TBL_BOARD WHERE
ROWNUM <= ${pageNum} * #{amount}
) TB JOIN TBL_MEMBER TM ON TB.USER_ID = TM.ID
WHERE TB.RN > (#{pageNum}-1) * #{amount}

mapper.insert:


SELECT NVL(MAX(BNO),0)+1 FROM TBL_BOARD

INSERT INTO TBL_BOARD(BNO, TITLE, CONTENT, USER_ID, SECRET)
VALUES( #{bno}
		, #{title}
		, #{content}
		, #{user_id}

		, CASE WHEN  #{secret} = 1 THEN '1' ELSE '0' END
		)

mapper.update:
UPDATE TBL_BOARD
SET   TITLE = #{title}
	, CONTENT = #{content}
	, USER_ID = #{user_id}
	, UPDATEDATE = SYSDATE
	, SECRET =#{secret}
WHERE BNO = #{bno}
