mapper.insert:
INSERT INTO TBL_MEMBER (USER_NO , NAME, ID, PW, PHONE, EMAIL, ADMIN)
VALUES((SELECT NVL(MAX(USER_NO),0)+1 from TBL_MEMBER)
		, #{name}
		, #{id}
		, #{pw}
		, #{phone}
		, #{email}
		, 0)

mapper.select:
SELECT COUNT(1)
FROM TBL_MEMBER 
WHERE ID = #{id}

mapper.update:
UPDATE TBL_MEMBER
	SET NAME = #{name}
		, id = #{id}	
		, PW = #{pw}
		, PHONE = #{phone}
		, EMAIL = #{email}
		, UPDATEDATE=SYSDATE
WHERE USER_NO = #{user_no}
