mapper.insert:
INSERT INTO TBL_REPLYS(RNO, BNO, REPLY, USER_ID)
VALUES((SELECT NVL(MAX(RNO),0)+1 from TBL_REPLYS)
		, #{bno}
		, #{reply}
		, #{user_id}
	   )

mapper.update:
UPDATE TBL_REPLYS
SET   REPLY = #{reply}
	, UPDATEDATE = SYSDATE
WHERE RNO = #{rno}

mapper.select:
SELECT RNO, BNO, REPLY, USER_ID, REPLYDATE, UPDATEDATE FROM ( SELECT /*+ INDEX_DESC(TBL_REPLYS, PK_REPLY) */ ROWNUM RN , RNO , BNO , REPLY , USER_ID , REPLYDATE , UPDATEDATE FROM TBL_REPLYS WHERE BNO = #{bno} AND
ROWNUM <= ${cri.pageNum} * #{cri.amount}
)
WHERE RN > (#{cri.pageNum}-1) * #{cri.amount}
